$(document).ready(function(){$("h2, h3").each(function(a){var c=$(this);var b=c.text().toLowerCase().trim().replace(/[\.,-\/#!?$%\^&\*;:{}=\-_`~()]/g,"").replace(/[ ]/g,"-");c.attr("id",b);c.prepend("<a href='#"+b+"' class='hash'>&#35;</a>")});$("img").each(function(a){var b=$(this);var c=$(this).attr("src");b.wrap("<a href='"+c+"'></a>")});$(".hash").click(function(b){b.preventDefault();var a=$(this).attr("href");$("html,body").animate({scrollTop:$(a).offset().top-86},500);window.history.pushState(null,null,a)});setTimeout(loadHash,1000)});function loadHash(){if(location.hash){var a=location.hash;$("html,body").animate({scrollTop:$(a).offset().top-86},500)}};