document.querySelectorAll("h2, h3").forEach(function(a){var b=a.innerHTML.toLowerCase().trim().replace(/[\.,-\/#!?$%\^&\*;:{}=\-_`~()]/g,"").replace(/[ ]/g,"-");a.id=b;a.innerHTML="<a href='#"+b+"' class='hash'>&#35;</a>"+a.innerHTML});document.querySelectorAll("img").forEach(function(a){var b=document.createElement("a");b.href=a.src;a.parentNode.insertBefore(b,a);b.appendChild(a)});
document.querySelectorAll(".hash").forEach(function(a){a.addEventListener("click",function(b){b.preventDefault();$("html,body").animate({scrollTop:a.offsetTop-86},500);window.history.pushState(null,null,a.href)})});setTimeout(function(){location.hash&&$("html,body").animate({scrollTop:document.getElementById(location.hash.substring(1)).offsetTop-86},500)},1E3);
