document.querySelectorAll("h2, h3").forEach(function(a){var b=a.innerHTML.toLowerCase().trim().replace(/[\.,-\/#!?$%\^&\*;:{}=\-_`~()]/g,"").replace(/[ ]/g,"-");a.id=b;a.innerHTML='<a href="#'+b+'" class="hash">&#35;</a>'+a.innerHTML});document.querySelectorAll("img").forEach(function(a){var b=document.createElement("a");b.href=a.src;a.parentNode.insertBefore(b,a);b.appendChild(a)});
document.querySelectorAll(".hash").forEach(function(a){a.addEventListener("click",function(b){b.preventDefault();scrollToY(a.offsetTop-86,500,"easeInOutQuint");window.history.pushState(null,null,a.href)})});setTimeout(function(){location.hash&&scrollToY(document.getElementById(location.hash.substring(1)).offsetTop-86,500,"easeInOutQuint")},1500);var headers=document.getElementsByTagName("HEADER"),topBtn=document.getElementById("to-top"),i=0;
function checkClasses(){for(i=0;i<headers.length;i++)headers[i].classList.toggle("header-shadow",0<window.pageYOffset);topBtn.classList.toggle("hidden",0==window.pageYOffset)}checkClasses();document.addEventListener("scroll",checkClasses);
